angular.module("kityminderEditor").controller("hyperlink.ctrl",function(e,t,l){e.R_URL=/(http|ftp|https):\/\/[\w\-_]+(\.[\w\-_]+)+([\w\-\.,@?^=%&amp;:\/~\+#]*[\w\-\@?^=%&amp;\/~\+#])?/;e.url=l.url||"";e.title=l.title||"";setTimeout(function(){var t=$("#link-url");t.focus();t[0].setSelectionRange(0,e.url.length)},30);e.shortCut=function(t){t.stopPropagation();if(t.keyCode==13){e.ok()}else if(t.keyCode==27){e.cancel()}};e.ok=function(){if(e.R_URL.test(e.url)){t.close({url:e.url,title:e.title})}else{e.urlPassed=false;var l=$("#link-url");l.focus();l[0].setSelectionRange(0,e.url.length)}editor.receiver.selectAll()};e.cancel=function(){t.dismiss("cancel");editor.receiver.selectAll()}});